// Login.js

import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const { email, password } = formData;

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async e => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/auth/login', {
        email,
        password
      });

      localStorage.setItem('token', res.data.token); // Store token in local storage

      // Redirect or update state to indicate logged in
      console.log('Logged in successfully'); // Example: redirect or update state
    } catch (err) {
      console.error(err.response.data); // Handle login error
    }
  };

  return (
    <form onSubmit={onSubmit}>
      <input type="email" placeholder="Email Address" name="email" value={email} onChange={onChange} required />
      <input type="password" placeholder="Password" name="password" value={password} onChange={onChange} minLength="6" />
      <button type="submit">Login</button>
    </form>
  );
};

export default Login;
