// src/pages/Home.js

import React, { useState } from 'react';
import axios from 'axios';
import styles from './Home.module.css';

const Home = () => {
  const [source, setSource] = useState('');
  const [destination, setDestination] = useState('');
  const [buses, setBuses] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/search', { source, destination });
      setBuses(response.data);
    } catch (err) {
      console.error('Error searching for buses', err);
    }
  };

  return (
    <div className={styles.homepage}>
      <header className={styles.header}>
        <div className={styles.headerContent}>
          <h1 className={styles.logo}>Project Reserva</h1>
          <div className={styles.authLinks}>
            <a href="/login" className={styles.authButton}>Login</a>
            <span className={styles.separator}>|</span>
            <a href="/register" className={styles.authButton}>Register</a>
          </div>
        </div>
      </header>

      <main className={styles.main}>
        <form className={styles.searchForm} onSubmit={handleSearch}>
          <div className={styles.formGroup}>
            <label htmlFor="source">Source:</label>
            <input
              type="text"
              id="source"
              value={source}
              onChange={(e) => setSource(e.target.value)}
              placeholder="Enter source"
              required
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="destination">Destination:</label>
            <input
              type="text"
              id="destination"
              value={destination}
              onChange={(e) => setDestination(e.target.value)}
              placeholder="Enter destination"
              required
            />
          </div>
          <button type="submit" className={styles.searchButton}>Search</button>
        </form>

        {buses.length > 0 && (
          <div className={styles.results}>
            <h2>Available Buses</h2>
            <ul>
              {buses.map(bus => (
                <li key={bus._id}>
                  {bus.source} to {bus.destination} - {bus.departureTime} to {bus.arrivalTime} - ${bus.price}
                </li>
              ))}
            </ul>
          </div>
        )}
      </main>

      <footer className={styles.footer}>
        <p>&copy; {new Date().getFullYear()} Project Reserva. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
